function revealAnswers() {

    if (document.getElementById("revealButton").innerText === "Reveal Hidden Cards") {

        for (let i = 0; i < hiddenCards.length; i++) {
            switch (hiddenCards[i]){
                case "team1":
                    if (_firstTeam===teamColor1) {
                        gridTracker[i] = 2;
                    } else {
                        gridTracker[i] = 3;
                    }
                    break;
                case "team2":
                    if (_firstTeam===teamColor1) {
                        gridTracker[i] = 3;
                    } else {
                        gridTracker[i] = 2;
                    }
                    break;
                case "assassin":
                    gridTracker[i] = 0;
                    break;
                case "bystander":
                    gridTracker[i] = 1;
                    break;
            }
        }
        rebuildGrid();
        document.getElementById("revealButton").innerText = "Hide Hidden Cards";
    } else {
        for (i = 0; i < hiddenCards.length; i++) {
            gridTracker[i] = 0;
        }
        rebuildGrid();
        document.getElementById("revealButton").innerText = "Reveal Hidden Cards";
    }
}

