function buildFirstGrid() {

    let k = 0;

    for (i = 0; i < gridHeight; i++) {
        for (j = 0; j < gridWidth; j++) {
            buildRectangle(cardColor1,j,i,k);
            gridTracker[k] = 0;
            k++;
        }
    }
}

function rebuildGrid() {

    let k = 0;

    for (let i = 0; i < gridHeight; i++) {
        for (let j = 0; j < gridWidth; j++) {
            switch (gridTracker[k]) {
                case 0:
                    buildRectangle(cardColor1,j,i,k);
                    break;
                case 1:
                    buildRectangle(cardColor2,j,i,k);
                    break;
                case 2:
                    buildRectangle(cardColor3,j,i,k);
                    break;
                case 3:
                    buildRectangle(cardColor4,j,i,k);
                    break;
                case 4:
                    buildRectangle(cardColor1,j,i,k);
                    gridTracker[k] = 0;
                    break;
            }
            k++;
        }
    }
}

function buildRectangle(myColor, j, i, k) {
    ctx.fillStyle = myColor;
    ctx.fillRect(j * (canvasWidth / gridWidth), i * (canvasHeight / gridHeight),(canvasWidth / gridWidth) - borderSpace,(canvasHeight / gridHeight) - borderSpace);
    let fontDefine = "small-caps bold "+fontSize+"px Arial";
    ctx.font=fontDefine;
    ctx.fillStyle = fontColor;

    ctx.fillText(wordList[k],j * (canvasWidth / gridWidth)+borderSpace,(i * (canvasHeight / gridHeight))+(canvasHeight / gridHeight / 2 + 5));

}

function shuffle(array) {
    let currentIndex = array.length;
    let temporaryValue, randomIndex;
    let i = randomCode;
    console.log ("random code = "+randomCode);
    // While there remain elements to shuffle...
    while (0 !== currentIndex) {

        // Pick a remaining element...
        randomIndex = Math.floor(randomIzer[i] * currentIndex);
        currentIndex -= 1;

        // And swap it with the current element.
        temporaryValue = array[currentIndex];
        array[currentIndex] = array[randomIndex];
        array[randomIndex] = temporaryValue;
        i++;
    }

    return array;
}

